def main():
    os.makedirs(OUT, exist_ok=True)
    for t in T:
        fn=f"blog{t['id']}.html"
        with open(os.path.join(OUT,fn),'w',encoding='utf-8') as f: f.write(gp(t))
        print(f"  {fn} ({t['n']})")
    print(f"\nDone! {len(T)} pages in {OUT}")

